function useTilt(active: boolean | null) {
  const ref = useRef(null);

  useEffect(() => {
    if (!ref.current || !active) {
      return;
    }

    const state = {
      rect: undefined,
      mouseX: undefined,
      mouseY: undefined,
    };

    const el = ref.current;

    const handleMouseMove = (e: {clientX: undefined; clientY: undefined}) => {
      if (!el) {
        return;
      }
      if (!state.rect) {
        // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
        // @ts-ignore
        state.rect = el.getBoundingClientRect();
      }
      state.mouseX = e.clientX;
      state.mouseY = e.clientY;
      // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
      // @ts-ignore
      const px = (state.mouseX - state.rect.left) / state.rect.width;
      // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
      // @ts-ignore
      const py = (state.mouseY - state.rect.top) / state.rect.height;
      // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
      // @ts-ignore
      el.style.setProperty('--px', px);
      // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
      // @ts-ignore
      el.style.setProperty('--py', py);
    };
    // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
    // @ts-ignore
    el.addEventListener('mousemove', handleMouseMove);

    return () => {
      // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
      // @ts-ignore
      el.removeEventListener('mousemove', handleMouseMove);
    };
  }, [active]);

  return ref;
}

const initialState = {
  slideIndex: 0,
};

// eslint-disable-next-line @typescript-eslint/ban-ts-ignore
// @ts-ignore
const slidesReducer = (state: {slideIndex: number}, event: {type: string}) => {
  if (event.type === 'NEXT') {
    return {
      ...state,
      slideIndex: (state.slideIndex + 1) % slides.length,
    };
  }
  if (event.type === 'PREV') {
    return {
      ...state,
      slideIndex: state.slideIndex === 0 ? slides.length - 1 : state.slideIndex - 1,
    };
  }
};

// eslint-disable-next-line @typescript-eslint/ban-ts-ignore
// @ts-ignore
function Slide({slide, offset}) {
  const active = offset === 0 ? true : null;
  const ref = useTilt(active);

  return (
    <div
      ref={ref}
      className="sli"
      data-active={active}
      style={{
        // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
        // @ts-ignore
        '--offset': offset,

        '--dir': offset === 0 ? 0 : offset > 0 ? 1 : -1,
      }}
    >
      <div
        className="slideBackground"
        style={{
          backgroundImage: `url('${slide.image}')`,
        }}
      />
      <div
        className="slideContent"
        style={{
          backgroundImage: `url('${slide.image}')`,
        }}
      >
        <div className="slideContentInner">
          <h2 className="slideTitle">{slide.title}</h2>
          <h3 className="slideSubtitle">{slide.subtitle}</h3>
          <p className="slideDescription">{slide.description}</p>
        </div>
      </div>
    </div>
  );
}

export function Slider() {
  // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
  // @ts-ignore
  const [state, dispatch] = useReducer(slidesReducer, initialState);

  return (
    <div className="sl">
      {/*  eslint-disable-next-line @typescript-eslint/ban-ts-ignore */}
      {/* @ts-ignore */}
      <button onClick={() => dispatch({type: 'NEXT'})}>‹</button>

      {[...slides, ...slides, ...slides].map((slide, i) => {
        const offset = slides.length + (state.slideIndex - i);
        return <Slide slide={slide} offset={offset} key={i} />;
      })}
      {/*  eslint-disable-next-line @typescript-eslint/ban-ts-ignore */}
      {/* @ts-ignore */}
      <button onClick={() => dispatch({type: 'PREV'})}>›</button>
    </div>
  );
}

