import React, {useState} from 'react';
import 'components/ImageAnimation/images/main.scss';
import './elipse.css';

export const Main = () => {
  // const params: ReactIdSwiperProps = {
  //   direction: 'vertical',
  //   ContainerEl: 'section',
  //   WrapperEl: 'section',
  //   autoHeight: true,
  //   roundLengths: true,
  //   fadeEffect: {
  //     // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
  //     // @ts-ignore
  //     Crossfade: true,
  //   },
  //   navigation: {
  //     nextEl: '.arros',
  //   },
  //   keyboard: {
  //     enabled: true,
  //     // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
  //     // @ts-ignore
  //     pageUpDown: true,
  //   },
  //   mousewheel: true,
  //   speed: 1000,
  // };

  const [slide, setSlide] = useState(0);
  // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
  // @ts-ignore
  function checkKey(e) {
    if (e.keyCode === 38) {
      setSlide(slide - 1);
    } else if (e.keyCode === 40) {
      setSlide(slide + 1);
    }
  }
  return (
    <>
      <div role="button" tabIndex={0} className="arros" onKeyDown={(e) => checkKey(e)}>
        <div className="slide1" style={slide >= 1 ? {opacity: 0} : {textShadow: '1px'}}>
          <h1 className="first-h1">
            <div className="dot-wraper">
              IDEAS <div className="dot" />
            </div>
            <div className="dot-wraper">
              STRATEGIES
              <div className="dot" />
            </div>
            <div className="dot-wraper">
              SOLUTIONS
              <div className="dot" />
            </div>
          </h1>
        </div>
        <div
          className="slide2"
          style={
            slide <= 1 ? {transform: `translateY(-${slide}00%)`} : {textShadow: '1px'}
          }
        >
          <div className="slide2-div1">
            <h1>
              We suggest powerful <br /> backend solutions
            </h1>
          </div>
          <div>
            <div className="slide2-div2">
              <h1>We suggest powerful backend solutions </h1>
            </div>
            <div className="slide2-div3">
              <h1>We suggest powerful backend solutions</h1>
            </div>
          </div>
        </div>
        <div className="slide3" />
      </div>
    </>
  );
};
