import React, {useState} from 'react';
import 'components/ImageAnimation/BurgerMenu/buregrMenu.css';
import {img, img2} from '../images/index';

import 'components/ImageAnimation/BurgerMenu/arrow.scss';

export const Burger = () => {
  const [head, sethead] = useState(0);
  // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
  // @ts-ignore
  function checkKey(e) {
    if (e.keyCode === 38) {
      sethead(head - 1);
    } else if (e.keyCode === 40) {
      sethead(head + 1);
    }
  }
  return (
    <>
      <div
        className="menu-wrap"
        role="button"
        tabIndex={0}
        onKeyDown={(e) => checkKey(e)}
      >
        <input type="checkbox" className="toggler" />
        <div className="hamburger">
          <div />
        </div>
        <div className="text">
          <p>media & communication</p>
        </div>
        <img className="imgs" src={head >= 3 ? img2 : img} alt="some" />

        {/* <div */}
        {/* className="arrow" */}
        {/* // onClick={(event) => { */}
        {/* //   setCounter(counter + 1); */}
        {/* //   scroll(event, counter); */}
        {/* // }} */}
        {/* > */}
        {/* <div className="arrow-top" /> */}
        {/* <div className="arrow-bottom" /> */}
        {/* </div> */}
        <div className="menu">
          <div>
            <div>
              <ul>
                <li>
                  <a href="/">About</a>
                </li>
                <li>
                  <a href="/">Career</a>
                </li>
                <li>
                  <a href="/">Projects</a>
                </li>
                <li>
                  <a href="/">Team</a>
                </li>
                <li>
                  <a href="/">Contacts</a>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
