import React, {useState} from 'react';
import 'components/ImageAnimation/BurgerMenu/buregrMenu.css';
// import {img, img2} from '../images/index';

import 'components/ImageAnimation/BurgerMenu/arrow.scss';

export const Burger = () => {
  const [slide, setSlide] = useState(0);
  // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
  // @ts-ignore

  function checkKey(e: React.KeyboardEvent<HTMLDivElement>) {
    if (e.keyCode === 38) {
      setSlide(slide - 1);
    } else if (e.keyCode === 40) {
      setSlide(slide + 1);
    }
  }
  if (slide < 0) {
    setSlide(0);
  }
  return (
    <>
      <div
        className="menu-wrap"
        role="button"
        tabIndex={0}
        onKeyDown={(e) => checkKey(e)}
      >
        <input type="checkbox" className="toggler" />
        <div className="hamburger">
          <div
            style={
              slide === 3
                ? {backgroundColor: '#0F72FF'}
                : slide === 4
                ? {backgroundColor: '#FF8E50'}
                : {backgroundColor: 'white'}
            }
          />
        </div>
        <div className="text" style={slide >= 3 ? {color: 'black'} : {color: 'white'}}>
          <p>media & communication</p>
        </div>
        <div
          className={`imgs${slide}`}
          // src={slide >= 3 ? img2 : img}
        />

        {/* <div */}
        {/* className="arrow" */}
        {/* // onClick={(event) => { */}
        {/* //   setCounter(counter + 1); */}
        {/* //   scroll(event, counter); */}
        {/* // }} */}
        {/* > */}
        {/* <div className="arrow-top" /> */}
        {/* <div className="arrow-bottom" /> */}
        {/* </div> */}
        <div className="menu">
          <div>
            <div>
              <ul>
                <li>
                  <a href="/">About</a>
                </li>
                <li>
                  <a href="/">Career</a>
                </li>
                <li>
                  <a href="/">Projects</a>
                </li>
                <li>
                  <a href="/">Team</a>
                </li>
                <li>
                  <a href="/">Contacts</a>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
