import React, {useState} from 'react';
import 'components/ImageAnimation/BurgerMenu/buregrMenu.css';
import {img, img2} from '../images/index';

import 'components/ImageAnimation/BurgerMenu/arrow.scss';

export const Burger = () => {
  const [slide, setSlide] = useState(0);
  // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
  // @ts-ignore
  function checkKey(e) {
    debugger
    if (e.keyCode === 38) {
      setSlide(slide - 1);
    } else if (e.keyCode === 40) {
      setSlide(slide + 1);
    }
  }
  return (
    <>
      {/* eslint-disable-next-line jsx-a11y/html-has-lang,jsx-a11y/no-static-element-interactions */}
      <html onKeyDown={(e) => checkKey(e)}>
        <div className="menu-wrap">
          <input type="checkbox" className="toggler" />
          <div className="hamburger">
            <div />
          </div>
          <div className="text">
            <p>media & communication</p>
          </div>
          <img className="imgs" src={slide >= 3 ? img : img2} alt="some" />

          {/* <div */}
          {/* className="arrow" */}
          {/* // onClick={(event) => { */}
          {/* //   setCounter(counter + 1); */}
          {/* //   scroll(event, counter); */}
          {/* // }} */}
          {/* > */}
          {/* <div className="arrow-top" /> */}
          {/* <div className="arrow-bottom" /> */}
          {/* </div> */}
          <div className="menu">
            <div>
              <div>
                <ul>
                  <li>
                    <a href="/">About</a>
                  </li>
                  <li>
                    <a href="/">Career</a>
                  </li>
                  <li>
                    <a href="/">Projects</a>
                  </li>
                  <li>
                    <a href="/">Team</a>
                  </li>
                  <li>
                    <a href="/">Contacts</a>
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </html>
    </>
  );
};
