import React, {useState} from 'react';
import 'components/ImageAnimation/images/main.scss';
import './elipse.css';
// import Navigation from "./swiper";

export const Main = () => {


  const [slide, setSlide] = useState(0);
  // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
  // @ts-ignore
  function checkKey(e) {
    if (e.keyCode === 38) {
      setSlide(slide - 1);
    } else if (e.keyCode === 40) {
      setSlide(slide + 1);
    }
  }

  return (
    <>
      <div role="button" tabIndex={0} className="arros" onKeyDown={(e) => checkKey(e)}>
        <div className="slide1" style={slide >= 1 ? {opacity: 0} : {textShadow: '1px'}}>
          <h1 className="first-h1">
            <div className="dot-wraper">
              IDEAS <div className="dot" />
            </div>
            <div className="dot-wraper">
              STRATEGIES
              <div className="dot" />
            </div>
            <div className="dot-wraper">
              SOLUTIONS
              <div className="dot" />
            </div>
          </h1>
        </div>
        <div className="slide2">
            <div className="flip-card-inner" style={slide>=2?{
              transform:" rotateX(180deg)"}:{textShadow: '1px'}}>
              <div
                  className="slide2-div1-front"
                  style={slide >= 1 ? {transform: `translateX(0%)`} : {textShadow: '1px'}}
              >
                <h1>
                  We suggest powerful <br /> backend solutions
                </h1>
              </div>
              <div className="slide2-div1-back ">
                <h1>
                  We suggest powerful <br /> backend solutions
                </h1>
              </div>
            </div>

            <div className="flip-card-inner-2" style={slide>=2?{
              transform:" rotateX(180deg)"}:{textShadow: '1px'}}>
              <div
                  className="slide2-div2-front"
                  style={slide >= 1 ? {transform: `translateX(120%)`} : {textShadow: '1px'}}
              >
                <h1>
                  We suggest powerful <br /> backend solutions
                </h1>
              </div>
              <div className="slide2-div2-back ">
                <h1>
                  We suggest powerful <br /> backend solutions
                </h1>
              </div>

            </div>
          <div className="flip-card-inner-3" style={slide>=2?{
            transform:" rotateX(180deg)"}:{textShadow: '1px'}}>
            <div
                className="slide2-div3-front"
                style={slide >= 1 ? {transform: `translateX(106.76%)`} : {textShadow: '1px'}}
            >
              <h1>
                We suggest powerful <br /> backend solutions
              </h1>
            </div>
            <div className="slide2-div3-back ">
              <h1>
                We suggest powerful <br /> backend solutions
              </h1>
            </div>

          </div>

        </div>
        <div className="slide3" >




        </div>
      </div>
    </>
  );
};
